import * as d3 from "d3";
import data from "./assets/music_genres_full_with_scraped_info.json";

const width = window.innerWidth;
const height = window.innerHeight;
const radius = width / 2;

const svg = d3
  .select("#tree")
  .attr("viewBox", [-width / 2, -height / 2, width, height])
  .style("font", "11px sans-serif"); // 字体略大一点更清晰

const minGrandchildCount = 5;

// ✅ 深色莫兰迪色系（更有层次）
const morandiColors = [
 "#7A6F5C", // olive taupe
  "#6B90A6", // steel blue
  "#7E9E75", // dusty green
  "#A86B6B", // muted rose
  "#C48756", // warm amber
  "#8E6F9C", // soft violet
  "#5C7A89", // slate teal
  "#B26A84", // antique pink
];

// ✅ D3 颜色比例尺
const colorScale = d3.scaleOrdinal(morandiColors);

function buildHierarchy(data) {
  const allowedCategories = ["Popular", "Classical"];

  const filteredData = data.filter((d) =>
    allowedCategories.includes(d.category)
  );

  return d3.hierarchy({
    name: "Music Genres",
    children: filteredData.map((d) => ({
      name: d.category,
      children: (d.subcategories || [])
        .map((sc) => {
          const genres = (sc.genres || [])
            .map((g) => {
              const children = g.children || [];
              return { name: g.name, children };
            })
            .filter((g) => g.children.length >= minGrandchildCount);

          if (genres.length < minGrandchildCount) return null;

          return { name: sc.subcategory || "General", children: genres };
        })
        .filter(Boolean),
    })),
  });
}

const root = buildHierarchy(data);
const tree = d3.tree().size([2 * Math.PI, radius - 150]);
tree(root);

// ✅ 获取顶层类别名函数
function getTopCategory(d) {
  const ancestors = d.ancestors();
  if (ancestors.length >= 2) return ancestors[1].data.name;
  return d.data.name;
}

// ✅ 连线：颜色与目标节点一致
svg.append("g")
  .attr("fill", "none")
  .attr("stroke-opacity", 0.6)
  .attr("stroke-width", 1)
  .selectAll("path")
  .data(root.links())
  .join("path")
  .attr("stroke", (d) => colorScale(getTopCategory(d.target)))
  .attr("d", d3.linkRadial().angle((d) => d.x).radius((d) => d.y));

// ✅ 节点
const node = svg.append("g")
  .selectAll("g")
  .data(root.descendants())
  .join("g")
  .attr("transform", (d) => `
      rotate(${(d.x * 180) / Math.PI - 90})
      translate(${d.y},0)
    `);

// 圆点颜色
node.append("circle")
  .attr("r", 10)
  .attr("fill", (d) => colorScale(getTopCategory(d)));

// ✅ 名称颜色：与节点颜色一致，但略深一点提高对比度
node.append("text")
  .attr("dy", "0.31em")
  .attr("x", (d) => (d.x < Math.PI === !d.children ? 6 : -6))
  .attr("text-anchor", (d) =>
    d.x < Math.PI === !d.children ? "start" : "end"
  )
  .attr("transform", (d) => (d.x >= Math.PI ? "rotate(180)" : null))
  // ✅ 设置字体大小（优先）
  .attr("font-size", (d) => {
    const bigNames = ["Folk", "Pop", "Rock", "Rap", "Electronic", "Classical", "Jazz"];
    return bigNames.includes(d.data.name) ? "100px" : "30px";
  })
  .attr("font-weight", (d) => {
    const bigNames = ["Folk", "Pop", "Rock", "Rap", "Electronic", "Classical", "Jazz"];
    return bigNames.includes(d.data.name) ? "600" : "400"; // 半粗体
  })
  .attr("fill", (d) => {
    const color = d3.color(colorScale(getTopCategory(d)));
    return color ? color.darker(0.6) : "#444";
  })
  .text((d) => d.data.name)
  // ✅ clone 一定要放在最后
  .clone(false)
  .lower()
  .attr("stroke", "white")
  .attr("stroke-width", 0.8);
