const data = {
  "Folk": {
    "total_plays": 44580532,
    "total_comments": 112595,
    "song_count": 50
  },
  "Pop": {
    "total_plays": 3393742080,
    "total_comments": 12806829,
    "song_count": 50
  },
  "Rock": {
    "total_plays": 186807824,
    "total_comments": 5122030,
    "song_count": 50
  },
  "Rap": {
    "total_plays": 519658432,
    "total_comments": 267719,
    "song_count": 50
  },
  "Electronic": {
    "total_plays": 405760640,
    "total_comments": 185764,
    "song_count": 50
  },
  "Classical": {
    "total_plays": 74785024,
    "total_comments": 383731,
    "song_count": 50
  },
  "Jazz": {
    "total_plays": 28298645,
    "total_comments": 98685,
    "song_count": 50
  },
  "ACG": {
    "total_plays": 89843048,
    "total_comments": 95537,
    "song_count": 50
  }
};

// ==================== 基础设置（响应式 + 统一常量） ====================
const width = 800;
const height = 600;
const margin = { top: 50, right: 170, bottom: 100, left: 100 };

const svg = d3.select("#bubble-svg")
  .attr("width", width)
  .attr("height", height)
  .attr("viewBox", `0 0 ${width} ${height}`)
  .attr("preserveAspectRatio", "xMidYMid meet");

// 一个分组容器，便于后续加网格等
const g = svg.append("g");

// ==================== 预处理与比例尺 ====================
// data 形如：{ "Pop": {total_plays, total_comments, song_count}, ... }
const genres = Object.keys(data);

// 计算数据范围
const playsExtent = d3.extent(genres, g => data[g].total_plays);
const commentsExtent = d3.extent(genres, g => data[g].total_comments);
const songsExtent = d3.extent(genres, g => data[g].song_count);

// 对数轴下限必须 > 0，给一点 padding
const xMin = Math.max(1, (playsExtent[0] || 1) * 0.8);
const xMax = (playsExtent[1] || 1) * 1.2;

const xScale = d3.scaleLog()
  .domain([xMin, xMax])
  .range([margin.left, width - margin.right])
  .nice();

const yScale = d3.scaleLinear()
  .domain([0, (commentsExtent[1] || 0) * 1.1])
  .range([height - margin.bottom, margin.top])
  .nice();

// 半径用平方根比例尺（面积呈线性）
const rScale = d3.scaleSqrt()
  .domain([Math.max(1, songsExtent[0] || 1), Math.max(2, songsExtent[1] || 2)])
  .range([8, 40]);

// 颜色：把域绑定到实际的流派列表
const colorScale = d3.scaleOrdinal()
  .domain(genres)
  .range(d3.schemeCategory10);

// ==================== 网格线（提升可读性） ====================
const xGrid = d3.axisBottom(xScale)
  .tickSize(-(height - margin.top - margin.bottom))
  .tickFormat("");

const yGrid = d3.axisLeft(yScale)
  .tickSize(-(width - margin.left - margin.right))
  .tickFormat("");

g.append("g")
  .attr("class", "grid x-grid")
  .attr("transform", `translate(0,${height - margin.bottom})`)
  .call(xGrid);

g.append("g")
  .attr("class", "grid y-grid")
  .attr("transform", `translate(${margin.left},0)`)
  .call(yGrid);

svg.selectAll(".grid line").attr("stroke", "#eaeaea");
svg.selectAll(".grid path").attr("stroke", "none");

// ==================== 坐标轴 ====================
const xFormat = d3.format(".2s");
const yFormat = d3.format(".2s");

// log 轴刻度自适应 + 保留常见层级
const xAxis = d3.axisBottom(xScale)
  .ticks(10, "~s");

const yAxis = d3.axisLeft(yScale)
  .ticks(6)
  .tickFormat(yFormat);

g.append("g")
  .attr("transform", `translate(0,${height - margin.bottom})`)
  .attr("class", "x-axis")
  .call(xAxis);

g.append("g")
  .attr("transform", `translate(${margin.left},0)`)
  .attr("class", "y-axis")
  .call(yAxis);

// ==================== 数据节点 ====================
const nodes = genres.map(genre => {
  const stats = data[genre];
  return {
    id: genre,
    total_plays: stats.total_plays,
    total_comments: stats.total_comments,
    song_count: stats.song_count,
    x: xScale(stats.total_plays),
    y: yScale(stats.total_comments),
    r: rScale(stats.song_count),
    active: true // 图例筛选状态
  };
});

// ==================== 悬浮提示（div tooltip） ====================
const tooltip = d3.select("body").append("div")
  .attr("class", "bubble-tooltip")
  .style("position", "absolute")
  .style("pointer-events", "none")
  .style("padding", "8px 10px")
  .style("border-radius", "8px")
  .style("background", "rgba(0,0,0,0.75)")
  .style("color", "#fff")
  .style("font", "12px/1.2 sans-serif")
  .style("opacity", 0);

// ==================== 绘制气泡 ====================
const bubbleLayer = g.append("g").attr("class", "bubble-layer");

const bubbles = bubbleLayer.selectAll(".bubble")
  .data(nodes, d => d.id)
  .enter().append("circle")
  .attr("class", "bubble")
  .attr("r", d => d.r)
  .attr("fill", d => colorScale(d.id))
  .attr("stroke", "#fff")
  .attr("stroke-width", 1.5)
  .on("mouseenter", (event, d) => {
    tooltip
      .style("opacity", 1)
      .html(
        `<div style="font-weight:600;margin-bottom:4px;">${d.id}</div>
         <div>Plays：${d3.format(",")(d.total_plays)}</div>
         <div>Comments：${d3.format(",")(d.total_comments)}</div>
         <div>Songs：${d3.format(",")(d.song_count)}</div>`
      );
  })
  .on("mousemove", (event) => {
    tooltip
      .style("left", (event.pageX + 14) + "px")
      .style("top",  (event.pageY + 14) + "px");
  })
  .on("mouseleave", () => {
    tooltip.style("opacity", 0);
  });

// ==================== 力导向布局 ====================
const simulation = d3.forceSimulation(nodes)
  .force("x", d3.forceX(d => xScale(d.total_plays)).strength(1))
  .force("y", d3.forceY(d => yScale(d.total_comments)).strength(1))
  .force("collide", d3.forceCollide(d => d.r + 3)) // 多 1px 间距
  .alpha(1)
  .alphaDecay(0.05)
  .on("tick", ticked);

function clamp(val, min, max) {
  return Math.max(min, Math.min(max, val));
}

function ticked() {
  bubbles
    .attr("cx", d => d.x = clamp(d.x, margin.left + d.r, width - margin.right - d.r))
    .attr("cy", d => d.y = clamp(d.y, margin.top + d.r, height - margin.bottom - d.r));
}

// ==================== 坐标轴标签 ====================
svg.append("text")
  .attr("x", width / 2)
  .attr("y", height - 50)
  .attr("text-anchor", "middle")
  .attr("class", "axis-label")
  .style("font", "12px sans-serif")
  .text("Total Plays (log)");

svg.append("text")
  .attr("transform", "rotate(-90)")
  .attr("x", -height / 2)
  .attr("y", 40)
  .attr("text-anchor", "middle")
  .attr("class", "axis-label")
  .style("font", "12px sans-serif")
  .text("Total Comments");

// ==================== 图例（可点击筛选） ====================
const legend = svg.append("g").attr("class", "legend-group");

const legendItem = legend.selectAll(".legend")
  .data(genres)
  .enter().append("g")
  .attr("class", "legend")
  .attr("transform", (d, i) => `translate(${width - margin.right + 10}, ${margin.top + i * 24})`)
  .style("cursor", "pointer")
  .on("click", (event, genre) => {
    // 切换该流派显示/隐藏
    const node = nodes.find(n => n.id === genre);
    node.active = !node.active;

    // 更新气泡可见性与透明度
    bubbles
      .filter(d => d.id === genre)
      .transition().duration(250)
      .attr("opacity", node.active ? 1 : 0.1);

    // 图例样式同步
    legendItem
      .filter(d => d === genre)
      .select("text")
      .transition().duration(200)
      .style("opacity", node.active ? 1 : 0.4);

    legendItem
      .filter(d => d === genre)
      .select("rect")
      .transition().duration(200)
      .style("opacity", node.active ? 1 : 0.4);

    // 力导向无需重新开动；只是视觉过滤
  });

legendItem.append("rect")
  .attr("width", 14)
  .attr("height", 14)
  .attr("rx", 3)
  .attr("ry", 3)
  .style("fill", d => colorScale(d));

legendItem.append("text")
  .attr("x", 20)
  .attr("y", 11)
  .text(d => d)
  .style("font-size", "12px")
  .style("font-family", "sans-serif");

// ==================== 初始放置（以免第一帧在 (0,0) 闪烁） ====================
bubbles
  .attr("cx", d => d.x)
  .attr("cy", d => d.y);

// ==================== 可选：窗口尺寸变化时，tooltip 处理 ====================
window.addEventListener("scroll", () => { tooltip.style("opacity", 0); });
