import * as d3 from "d3";

function Sunburst(data, {
  children = d => d.children,
  value = d => d.value,
  label = d => d.name,
  title = d => `${d.ancestors().map(d => d.data.name).join(" → ")}\n${d.value}`,
  width = 1600,
  height = 1600,
  innerRadius = 0,
  padAngle = 0,
  color = null, // 默认使用彩虹色方案
  fillOpacity = 0.7,
  stroke = "#fff",
  strokeWidth = 1.5,
} = {}) {
  const radius = Math.min(width, height) / 2;
  
  // 创建层次结构
  const root = d3.hierarchy(data)
    .sum(d => {
      const val = value(d);
      return (val && !isNaN(val) && val > 0) ? val : 1;
    })
    .sort((a, b) => (b.value || 0) - (a.value || 0));
  
  // 创建partition布局
  const partition = d3.partition()
    .size([2 * Math.PI, radius]);
  
  const arc = d3.arc()
    .innerRadius(d => Math.max(0, d.y0 + innerRadius))
    .outerRadius(d => d.y1)
    .startAngle(d => d.x0)
    .endAngle(d => d.x1)
    .padAngle(padAngle);
  
  // 计算布局
  partition(root);
  
  // 修复角度：确保每一层都填满父节点
  function fixAngles(node, startAngle, endAngle) {
    if (!node.children || node.children.length === 0) {
      return;
    }
    
    const children = node.children;
    const angleRange = endAngle - startAngle;
    const childrenValueSum = children.reduce((sum, child) => sum + (child.value || 0), 0);
    
    if (childrenValueSum <= 0) {
      return;
    }
    
    // 按值比例分配角度
    let currentAngle = startAngle;
    children.forEach((child, index) => {
      const childAngle = (child.value / childrenValueSum) * angleRange;
      child.x0 = currentAngle;
      child.x1 = currentAngle + childAngle;
      currentAngle = child.x1;
      
      // 递归修复子节点
      fixAngles(child, child.x0, child.x1);
    });
    
    // 确保最后一个填满
    if (children.length > 0) {
      children[children.length - 1].x1 = endAngle;
    }
  }
  
  // 修复第一层：填满2π
  if (root.children && root.children.length > 0) {
    const firstLevel = root.children;
    const firstLevelValueSum = firstLevel.reduce((sum, child) => sum + (child.value || 0), 0);
    if (firstLevelValueSum > 0) {
      let currentAngle = 0;
      firstLevel.forEach(child => {
        const angle = (child.value / firstLevelValueSum) * (2 * Math.PI);
        child.x0 = currentAngle;
        child.x1 = currentAngle + angle;
        currentAngle = child.x1;
        
        // 递归修复子节点
        fixAngles(child, child.x0, child.x1);
      });
      firstLevel[firstLevel.length - 1].x1 = 2 * Math.PI;
    }
  }
  
  // 创建SVG
  const svg = d3.select("svg")
    .attr("width", width)
    .attr("height", height)
    .attr("viewBox", [0, 0, width, height])
    .attr("style", "max-width: 100%; height: auto;");
  
  svg.selectAll("*").remove();
  
  const g = svg.append("g")
    .attr("transform", `translate(${width / 2},${height / 2})`);
  
  // 使用更美观的颜色方案 - 基于彩虹色渐变
  let colorScale;
  if (color && typeof color === "function") {
    // 如果传入了自定义颜色函数，使用它
    colorScale = color;
  } else {
    // 否则使用彩虹色渐变方案
    // 获取第一层节点的名称
    const firstLevelNames = root.children ? root.children.map(d => d.data.name) : [];
    const colorCount = Math.max(firstLevelNames.length, 10);
    
    // 使用彩虹色渐变生成颜色
    const rainbowColors = d3.quantize(d3.interpolateRainbow, colorCount);
    
    // 创建颜色映射
    colorScale = d3.scaleOrdinal()
      .domain(firstLevelNames)
      .range(rainbowColors);
  }
  
  // 绘制弧
  const path = g.selectAll("path")
    .data(root.descendants().filter(d => d.depth > 0))
    .join("path")
      .attr("fill", d => {
        let node = d;
        while (node.depth > 1) node = node.parent;
        return colorScale(node.data.name);
      })
      .attr("fill-opacity", fillOpacity)
      .attr("stroke", stroke)
      .attr("stroke-width", strokeWidth)
      .attr("d", arc)
      .append("title")
        .text(title);
  
  // 添加标签 - 改进显示逻辑
  // 不同层级使用不同的最小角度和半径阈值
  const getMinAngleForDepth = (depth) => {
    if (depth === 1) return Math.PI / 180 * 3; // 第一层：3度
    if (depth === 2) return Math.PI / 180 * 2; // 第二层：2度
    return Math.PI / 180 * 1.5; // 第三层及以下：1.5度
  };
  
  const getMinRadiusForDepth = (depth) => {
    if (depth === 1) return radius * 0.1; // 第一层：10%半径
    if (depth === 2) return radius * 0.2; // 第二层：20%半径
    return radius * 0.3; // 第三层及以下：30%半径
  };
  
  // 按深度分组处理标签，避免重叠
  const textData = root.descendants().filter(d => {
    if (!d.depth || d.value <= 0) return false;
    const angle = d.x1 - d.x0;
    const avgRadius = (d.y0 + d.y1) / 2;
    const minAngle = getMinAngleForDepth(d.depth);
    const minRadius = getMinRadiusForDepth(d.depth);
    return angle > minAngle && avgRadius > minRadius;
  });
  
  // 按深度排序，先显示深层，再显示浅层
  textData.sort((a, b) => b.depth - a.depth);
  
  const text = g.selectAll("text")
    .data(textData)
    .join("text")
      .attr("transform", d => {
        const ang = (d.x0 + d.x1) / 2;
        const x = (d.y0 + d.y1) / 2 * Math.sin(ang);
        const y = -(d.y0 + d.y1) / 2 * Math.cos(ang);
        return `translate(${x},${y})`;
      })
      .attr("dy", "0.35em")
      .attr("text-anchor", d => {
        const ang = (d.x0 + d.x1) / 2;
        return ang < Math.PI ? "start" : "end";
      })
      .attr("font-size", d => {
        // 按层级逐渐缩小字体
        if (d.depth === 1) return "11px";
        if (d.depth === 2) return "11px";
        if (d.depth === 3) return "10px";
        if (d.depth === 4) return "9px";
        return "8px"; // 更深层级使用更小字体
      })
      .attr("font-weight", "normal") // 所有层级使用正常字体粗细
      .attr("fill", "#333")
      .attr("pointer-events", "none") // 避免标签阻挡鼠标事件
      .text(d => {
        const name = label(d.data);
        // 根据深度调整最大长度
        const maxLength = d.depth === 1 ? 25 : d.depth === 2 ? 20 : 15;
        return name.length > maxLength ? name.substring(0, maxLength) + "..." : name;
      });
  
  return svg.node();
}

export default Sunburst;
