import * as d3 from "d3";

// 绘制旭日图
function drawSunburst(data, {
  width = 800,
  height = 800,
  label = d => d.name,
  value = d => d.value,
  opacity = 0.7,
  strokeColor = "#fff",
  strokeThickness = 1.5,
  padding = 0
} = {}) {

  const radius = Math.min(width, height) / 2;

  const root = d3.hierarchy(data)
    .sum(d => value(d))
    .sort((a, b) => (b.value || 0) - (a.value || 0));

  const partition = d3.partition().size([2 * Math.PI, radius]);

  const arc = d3.arc()
    .innerRadius(d => Math.max(0, d.y0))
    .outerRadius(d => d.y1)
    .startAngle(d => d.x0)
    .endAngle(d => d.x1)
    .padAngle(padding);

  partition(root);

  const svg = d3.select("#sunburst-chart")
    .attr("width", width)
    .attr("height", height)
    .attr("viewBox", [0, 0, width, height]);

  svg.selectAll("*").remove();  // 清空之前的内容

  const group = svg.append("g")
    .attr("transform", `translate(${width / 2}, ${height / 2})`);

  const colorScale = d3.scaleOrdinal(d3.schemeCategory10); // 为每个扇形分配颜色

  const paths = group.selectAll("path")
    .data(root.descendants().filter(d => d.depth > 0))
    .join("path")
    .attr("fill", d => colorScale(d.depth))  // 深度不同分配不同颜色
    .attr("d", arc)
    .attr("fill-opacity", opacity)
    .attr("stroke", strokeColor)
    .attr("stroke-width", strokeThickness);

  // 为每个路径添加工具提示
  paths.append("title")
    .text(d => `${label(d.data)}: ${value(d)} items`);

  // 为合适的扇形添加标签
  group.selectAll("text")
    .data(root.descendants().filter(d => d.depth > 1 && (d.x1 - d.x0) > 0.02))  // 过滤小于一定角度的扇形
    .join("text")
    .attr("transform", d => {
      const angle = (d.x0 + d.x1) / 2;
      const x = (d.y0 + d.y1) / 2 * Math.sin(angle);
      const y = -(d.y0 + d.y1) / 2 * Math.cos(angle);
      return `translate(${x}, ${y})`;
    })
    .attr("dy", "0.35em")
    .attr("text-anchor", d => {
      const angle = (d.x0 + d.x1) / 2;
      return angle < Math.PI ? "start" : "end";
    })
    .text(d => d.data.name)
    .style("font-size", "12px")
    .style("fill", "#333");

}

export { drawSunburst };
