// map-visualization.js (替换整个文件)
const width = 1000, height = 600;
const svg = d3.select("#map");
const tooltip = d3.select("#map-tooltip");

const projection = d3.geoNaturalEarth1()
  .scale(180)
  .translate([width / 2, height / 2]);

const geoPath = d3.geoPath().projection(projection);
const color = d3.scaleOrdinal(d3.schemeTableau10);

// 位置到经纬度（可继续补充）
const regionCoords = {
  Andalusi: [-4.8, 37.9],
  India: [78.96, 20.59],
  Korea: [127.8, 36.5],
  Iran: [53.7, 32.4],
  Turkey: [35, 39],
  "Middle East": [44, 33],
  Africa: [20, 0],
  Europe: [10, 50],
  China: [104, 35],
  Japan: [138, 37],
  "North America": [-100, 40],
  "South America": [-60, -15]
};

// 地区到洲的映射（筛选使用；可按需增补同义/别名）
const locationToContinent = {
  Andalusi: "Europe",
  Spain: "Europe",
  Portugal: "Europe",
  Europe: "Europe",

  India: "Asia",
  China: "Asia",
  Japan: "Asia",
  Korea: "Asia",
  Iran: "Asia",
  Turkey: "Asia",
  "Middle East": "Asia",

  Africa: "Africa",

  "North America": "North America",
  USA: "North America",
  "United States": "North America",
  Canada: "North America",

  "South America": "South America",
  Brazil: "South America",
  Argentina: "South America"
};

function getContinent(region) {
  return locationToContinent[region] || null;
}

// 1) 无论如何先把底图画出来
d3.json("https://raw.githubusercontent.com/holtzy/D3-graph-gallery/master/DATA/world.geojson")
  .then(world => {
    svg.append("g")
      .selectAll("path")
      .data(world.features)
      .join("path")
      .attr("d", geoPath)
      .attr("fill", "#e5edf3")
      .attr("stroke", "#888")
      .attr("stroke-width", 0.5);
  })
  .catch(err => {
    console.error("加载世界底图失败：", err);
  });

// 2) 再加载数据并绘制点和传播线
d3.json("music_genres_full_with_scraped_info.json")
  .then(data => {
    const genres = [];
    data.forEach(cat => {
      cat.subcategories?.forEach(sub => {
        sub.genres?.forEach(g => {
          const loc = g.scraped_info?.location;
          if (loc) {
            const cont = getContinent(loc);
            genres.push({
              name: g.name,
              category: cat.category,
              region: loc,
              continent: cont,
              artist: g.children?.[0]?.name || "未知",
              year: g.scraped_info?.years?.[0] || "不详"
            });
          }
        });
      });
    });

    // 主绘制函数
    function drawMap(continent = "all") {
      svg.selectAll(".circle, .route, .empty-tip").remove();

      const filtered = genres.filter(g => {
        if (continent === "all") return true;
        return g.continent === continent;
      });

      if (filtered.length === 0) {
        svg.append("text")
          .attr("class", "empty-tip")
          .attr("x", width / 2)
          .attr("y", height / 2)
          .attr("text-anchor", "middle")
          .attr("font-size", 14)
          .attr("fill", "#666")
          .text("没有符合筛选条件的流派（可尝试选择“全部”或补充 location→continent 映射）");
        return;
      }

      filtered.forEach(d => {
        const origin = regionCoords[d.region];
        if (!origin) return;

        const [x1, y1] = projection(origin);
        const spread = [
          x1 + (Math.random() - 0.5) * 100,
          y1 + (Math.random() - 0.5) * 60
        ];

        svg.append("path")
          .attr("class", "route")
          .attr(
            "d",
            `M${x1},${y1} Q${(x1 + spread[0]) / 2},${(y1 + spread[1]) / 2 - 30} ${spread[0]},${spread[1]}`
          )
          .attr("stroke", color(d.category))
          .attr("stroke-width", 1.5)
          .attr("opacity", 0.6)
          .attr("fill", "none");

        svg.append("circle")
          .attr("class", "circle")
          .attr("cx", x1)
          .attr("cy", y1)
          .attr("r", 5)
          .attr("fill", color(d.category))
          .attr("stroke", "#222")
          .attr("opacity", 0.85)
          .on("mouseover", (event) => {
            tooltip.transition().duration(150).style("opacity", 1);
            tooltip
              .html(
                `<b>${d.name}</b><br>地域：${d.region}<br>艺术家：${d.artist}<br>年代：${d.year}`
              )
              .style("left", event.pageX + 12 + "px")
              .style("top", event.pageY + "px");
          })
          .on("mouseout", () => tooltip.transition().duration(200).style("opacity", 0));
      });
    }

    // 默认显示全部
    drawMap("all");

    // 洲筛选
    d3.select("#continentSelect").on("change", function () {
      drawMap(this.value);
    });
  })
  .catch(err => {
    console.error("加载音乐流派数据失败：", err);
    // 给出可视化提示
    svg.append("text")
      .attr("x", width / 2)
      .attr("y", height / 2)
      .attr("text-anchor", "middle")
      .attr("font-size", 14)
      .attr("fill", "#666")
      .text("无法加载 music_genres_full_with_scraped_info.json（请通过本地服务器访问）");
  });
