import * as d3 from "d3";
import data from "./assets/music_genres_full_with_scraped_info.json";

const width = window.innerWidth;
const height = window.innerHeight;
const radius = width / 2;

const svg = d3
  .select("#tree")
  .attr("viewBox", [-width / 2, -height / 2, width, height])
  .style("font", "11px sans-serif");

// ✅ 不同类别的过滤门槛
const minGrandchildCount = 5;
const minGrandchildCountClassical = 1;
const minGrandchildCountElectronic = 8;

// ✅ 深色莫兰迪色系
const morandiColors = [
  "#7A6F5C", "#6B90A6", "#7E9E75", "#A86B6B",
  "#C48756", "#8E6F9C", "#5C7A89", "#B26A84",
];

const colorScale = d3.scaleOrdinal(morandiColors);

function getThreshold(categoryName, subcategoryName) {
  const name = (subcategoryName || categoryName || "").toLowerCase();
  if (name.includes("electronic") || name.includes("electro")) {
    return minGrandchildCountElectronic;
  }
  if (categoryName === "Classical") {
    return minGrandchildCountClassical;
  }
  return minGrandchildCount;
}

function buildHierarchy(data) {
  const allowedCategories = ["Popular", "Classical"];
  const filteredData = data.filter((d) => allowedCategories.includes(d.category));

  return d3.hierarchy({
    name: "Music Genres",
    children: filteredData.map((d) => ({
      name: d.category,
      children: (d.subcategories || [])
        .map((sc) => {
          const threshold = getThreshold(d.category, sc.subcategory);
          const genres = (sc.genres || [])
            .map((g) => ({ name: g.name, children: g.children || [] }))
            .filter((g) => g.children.length >= threshold);
          if (genres.length < threshold) return null;
          return { name: sc.subcategory || "General", children: genres };
        })
        .filter(Boolean),
    })),
  });
}

const root = buildHierarchy(data);
const tree = d3.tree().size([2 * Math.PI, radius - 150]);
tree(root);

function getTopCategory(d) {
  const strictNames = ["Rock", "Classical", "Electronic", "Popular"];
  const fuzzyNames = ["Rap", "Jazz", "Folk"];
  const excludeNames = ["Trap"];
  
  const ancestors = d.ancestors();
  for (const ancestor of ancestors) {
    const name = ancestor.data.name.toLowerCase();
    const strictMatch = strictNames.find(keyword => name === keyword.toLowerCase());
    if (strictMatch) return strictMatch;
    const isExcluded = excludeNames.some(keyword => name.includes(keyword.toLowerCase()));
    if (isExcluded) continue;
    const fuzzyMatch = fuzzyNames.find(keyword => name.includes(keyword.toLowerCase()));
    if (fuzzyMatch) return fuzzyMatch;
  }
  return d.data.name;
}

// ✅ 判断是否是顶层分类（Popular/Classical）
function isTopLevelCategory(d) {
  const name = d.data.name.toLowerCase();
  return name === "popular" || name === "classical";
}

// ✅ 判断是否高亮的辅助函数
function isHighlightedNode(d) {
  const strictNames = ["Rock", "Classical", "Electronic", "Popular"];
  const fuzzyNames = ["Rap", "Jazz", "Folk"];
  const excludeNames = ["Trap"];
  const name = d.data.name.toLowerCase();
  
  const isExcluded = excludeNames.some(keyword => name.includes(keyword.toLowerCase()));
  if (isExcluded) return false;
  
  const isStrictMatch = strictNames.some(keyword => name === keyword.toLowerCase());
  const isFuzzyMatch = fuzzyNames.some(keyword => name.includes(keyword.toLowerCase()));
  
  return isStrictMatch || isFuzzyMatch;
}

// ✅ 连线
svg.append("g")
  .attr("fill", "none")
  .attr("stroke-opacity", 0.6)
  .attr("stroke-width", 5)
  .selectAll("path")
  .data(root.links())
  .join("path")
  .attr("stroke", (d) => colorScale(getTopCategory(d.target)))
  .attr("d", d3.linkRadial().angle((d) => d.x).radius((d) => d.y));

// ✅ 节点
const node = svg.append("g")
  .selectAll("g")
  .data(root.descendants())
  .join("g")
  .attr("transform", (d) => `
      rotate(${(d.x * 180) / Math.PI - 90})
      translate(${d.y},0)
    `);

// ✅ 动态圆点半径
const maxBranchSize = d3.max(root.descendants(), d => d.descendants().length);
const sizeScale = d3.scaleSqrt().domain([1, maxBranchSize]).range([10, 50]);

node.append("circle")
  .attr("r", (d) => sizeScale(d.descendants().length))
  .attr("fill", (d) => colorScale(getTopCategory(d)))
  .attr("stroke", "#fff")
  .attr("stroke-width", 1.2);

// ✅ 文字标签（修复遮挡问题）
node.append("text")
  .attr("dy", (d) => {
    // Popular 和 Classical 垂直偏移，错开位置
    if (d.data.name.toLowerCase() === "popular") return "-30px";
    if (d.data.name.toLowerCase() === "classical") return "50px";
    return "0.31em";
  })
  .attr("x", (d) => {
    // Popular 和 Classical 用较小的水平距离
    if (isTopLevelCategory(d)) return d.x < Math.PI === !d.children ? 10 : -10;
    if (isHighlightedNode(d)) return d.x < Math.PI === !d.children ? 60 : -60;
    return d.x < Math.PI === !d.children ? 6 : -6;
  })
  .attr("text-anchor", (d) =>
    d.x < Math.PI === !d.children ? "start" : "end"
  )
  .attr("transform", (d) => (d.x >= Math.PI ? "rotate(180)" : null))
  .attr("font-size", (d) => {
    // Popular 和 Classical 字体稍小
    if (isTopLevelCategory(d)) return "90px";
    if (isHighlightedNode(d)) return "90px";
    return "30px";
  })
  .attr("font-weight", (d) => isHighlightedNode(d) ? "600" : "400")
  .attr("fill", (d) => {
    const color = d3.color(colorScale(getTopCategory(d)));
    return color ? color.darker(0.6) : "#444";
  })
  .text((d) => d.depth === 0 ? "" : d.data.name)
  .clone(false)
  .lower()
  .attr("stroke", "white")
  .attr("stroke-width", 0.8);
