// ==============================

(function() {
  'use strict';

  // ==============================
  // 第2层：点阵纹理生成
  // ==============================
  function initDotPattern() {
    let canvas = document.getElementById('dot-pattern');
    if (!canvas) {
      canvas = document.createElement('canvas');
      canvas.id = 'dot-pattern';
      canvas.className = 'bg-layer';
      canvas.style.cssText = 'position:fixed;top:0;left:0;width:100%;height:100%;z-index:1;pointer-events:none;opacity:0.6;';
      document.body.appendChild(canvas);
    }
    
    const ctx = canvas.getContext('2d');
    const dpr = window.devicePixelRatio || 1;
    let width = window.innerWidth;
    let height = window.innerHeight;
    
    function draw() {
      canvas.width = width * dpr;
      canvas.height = height * dpr;
      ctx.scale(dpr, dpr);
      canvas.style.width = width + 'px';
      canvas.style.height = height + 'px';
      
      const spacing = 20; // 点间距
      const dotSize = 1.5; // 点大小
      const opacity = 0.08; // 透明度
      
      ctx.fillStyle = `rgba(139, 92, 246, ${opacity})`; // 紫色点
      
      for (let x = 0; x < width; x += spacing) {
        for (let y = 0; y < height; y += spacing) {
          ctx.beginPath();
          ctx.arc(x, y, dotSize, 0, Math.PI * 2);
          ctx.fill();
        }
      }
    }
    
    draw();
    
    // 窗口大小改变时重新绘制
    let resizeTimer;
    window.addEventListener('resize', () => {
      clearTimeout(resizeTimer);
      resizeTimer = setTimeout(() => {
        width = window.innerWidth;
        height = window.innerHeight;
        draw();
      }, 100);
    });
  }

  // ==============================
  // 第3层：动态波纹/流光动画
  // ==============================
  function initWaveAnimation() {
    let canvas = document.getElementById('wave-animation');
    if (!canvas) {
      canvas = document.createElement('canvas');
      canvas.id = 'wave-animation';
      canvas.className = 'bg-layer';
      canvas.style.cssText = 'position:fixed;top:0;left:0;width:100%;height:100%;z-index:2;pointer-events:none;opacity:0.4;';
      document.body.appendChild(canvas);
    }
    
    const ctx = canvas.getContext('2d');
    const dpr = window.devicePixelRatio || 1;
    let width = window.innerWidth;
    let height = window.innerHeight;
    let time = 0;
    let animationId = null;
    
    function resize() {
      width = window.innerWidth;
      height = window.innerHeight;
      canvas.width = width * dpr;
      canvas.height = height * dpr;
      ctx.scale(dpr, dpr);
      canvas.style.width = width + 'px';
      canvas.style.height = height + 'px';
    }
    
    resize();
    
    window.addEventListener('resize', () => {
      resize();
    });
    
    function drawWave() {
      ctx.clearRect(0, 0, width, height);
      
      // 创建多个波形层
      const waves = [
        { amplitude: 30, frequency: 0.01, speed: 0.02, color: 'rgba(88, 101, 242, 0.3)', offset: 0 },
        { amplitude: 40, frequency: 0.008, speed: 0.015, color: 'rgba(139, 92, 246, 0.25)', offset: Math.PI / 3 },
        { amplitude: 25, frequency: 0.012, speed: 0.025, color: 'rgba(59, 130, 246, 0.2)', offset: Math.PI / 2 }
      ];
      
      waves.forEach((wave, index) => {
        ctx.beginPath();
        ctx.strokeStyle = wave.color;
        ctx.lineWidth = 2;
        
        const yBase = height * (0.3 + index * 0.2);
        
        for (let x = 0; x < width; x += 2) {
          const y = yBase + 
            Math.sin(x * wave.frequency + time * wave.speed + wave.offset) * wave.amplitude +
            Math.sin(x * wave.frequency * 2 + time * wave.speed * 1.5) * wave.amplitude * 0.5;
          
          if (x === 0) {
            ctx.moveTo(x, y);
          } else {
            ctx.lineTo(x, y);
          }
        }
        
        ctx.stroke();
        
        // 添加渐变填充
        const gradient = ctx.createLinearGradient(0, yBase - wave.amplitude, 0, yBase + wave.amplitude);
        gradient.addColorStop(0, wave.color.replace('0.3', '0.1').replace('0.25', '0.08').replace('0.2', '0.06'));
        gradient.addColorStop(1, wave.color.replace('0.3', '0').replace('0.25', '0').replace('0.2', '0'));
        
        ctx.fillStyle = gradient;
        ctx.fill();
      });
      
      // 横向流动的光带
      const lightBands = 3;
      for (let i = 0; i < lightBands; i++) {
        const bandY = (height / (lightBands + 1)) * (i + 1);
        const bandWidth = width * 0.3;
        const bandX = (time * 100 + i * width * 0.3) % (width + bandWidth) - bandWidth;
        
        const bandGradient = ctx.createLinearGradient(bandX, bandY - 20, bandX + bandWidth, bandY + 20);
        bandGradient.addColorStop(0, 'rgba(88, 101, 242, 0)');
        bandGradient.addColorStop(0.5, 'rgba(88, 101, 242, 0.15)');
        bandGradient.addColorStop(1, 'rgba(88, 101, 242, 0)');
        
        ctx.fillStyle = bandGradient;
        ctx.fillRect(bandX, bandY - 20, bandWidth, 40);
      }
      
      time += 0.016; // 约60fps
      animationId = requestAnimationFrame(drawWave);
    }
    
    drawWave();
    
    // 页面可见性API，隐藏时暂停动画
    document.addEventListener('visibilitychange', () => {
      if (document.hidden) {
        if (animationId) {
          cancelAnimationFrame(animationId);
          animationId = null;
        }
      } else {
        if (!animationId) {
          drawWave();
        }
      }
    });
  }

  // ==============================
  // 初始化
  // ==============================
  function init() {
    if (document.readyState === 'loading') {
      document.addEventListener('DOMContentLoaded', () => {
        initDotPattern();
        initWaveAnimation();
      });
    } else {
      initDotPattern();
      initWaveAnimation();
    }
  }

  init();
})();
