import * as d3 from "d3";

// 旭日图绘制函数
export function renderSunburst(data, {
  chartWidth = 800,
  chartHeight = 800,
  getLabel = d => d.name,
  getValue = d => d.value,
  opacity = 0.6,
  strokeColor = "#fff",
  strokeThickness = 1.5,
  anglePadding = 0
} = {}) {

  const radius = Math.min(chartWidth, chartHeight) / 2;

  const root = d3.hierarchy(data)
    .sum(d => getValue(d))
    .sort((a, b) => (b.value || 0) - (a.value || 0));

  const partition = d3.partition().size([2 * Math.PI, radius]);

  const arc = d3.arc()
    .innerRadius(d => Math.max(0, d.y0))
    .outerRadius(d => d.y1)
    .startAngle(d => d.x0)
    .endAngle(d => d.x1)
    .padAngle(anglePadding);

  partition(root);

  const svg = d3.select("svg")
    .attr("width", chartWidth)
    .attr("height", chartHeight)
    .attr("viewBox", [0, 0, chartWidth, chartHeight]);

  svg.selectAll("*").remove();

  const group = svg.append("g")
    .attr("transform", `translate(${chartWidth / 2}, ${chartHeight / 2})`);

  const colorScale = d3.scaleOrdinal(d3.schemeCategory10); // 使用颜色方案

  const paths = group.selectAll("path")
    .data(root.descendants().filter(d => d.depth > 0))
    .join("path")
    .attr("fill", d => colorScale(d.depth))  // 根据深度为每个扇区上色
    .attr("d", arc)
    .attr("fill-opacity", opacity)
    .attr("stroke", strokeColor)
    .attr("stroke-width", strokeThickness);

  // 添加每个扇区的工具提示
  paths.append("title")
    .text(d => `${getLabel(d.data)}: ${getValue(d)} items`);

  // 为合适的扇区添加标签
  group.selectAll("text")
    .data(root.descendants().filter(d => d.depth > 1 && (d.x1 - d.x0) > 0.02))  // 过滤掉过小的扇区
    .join("text")
    .attr("transform", d => {
      const angle = (d.x0 + d.x1) / 2;
      const x = (d.y0 + d.y1) / 2 * Math.sin(angle);
      const y = -(d.y0 + d.y1) / 2 * Math.cos(angle);
      return `translate(${x}, ${y})`;
    })
    .attr("dy", "0.35em")
    .attr("text-anchor", d => {
      const angle = (d.x0 + d.x1) / 2;
      return angle < Math.PI ? "start" : "end";
    })
    .text(d => getLabel(d.data))
    .style("font-size", "12px")
    .style("fill", "#333");

  return svg.node();
}
